function plot(){
    let dataX = d3.scaleLinear()
        .domain([0, 500])
        .range([0, width + margin.left]);
    let dataY = d3.scaleLinear()
        .domain([0, height + margin.top])
        .range([height + margin.top, 0]);

    let data = data.map(function(item){
        return {dataX: dataX(item.type), dataY: item.data};
    });

    let svg = d3.svg()
        .selectAll("path")
        .data(data)
        .enter()
        .append("path")
        .attr("class", "label")
        .attr("d", function(d){
            return "M " + xScale(d.data.dataX) + " M " + yScale(d.data.dataY) + " L " + yScale(d.data.dataY) + " L " + yScale(d.data.dataY) + " Z";
        })
        .attr("transform", function(d){
            return "translate(" + xScale(d.data.dataX) + " ," + yScale(d.data.dataY) + " )";
        });

    svg.exit()
        .on("click", function(d){
            console.log(d.data.dataX + " " + d.data.dataY);
        })
        .enter()
        .append("text")
        .text(function(data){
            return data.dataX.text() + " - " + data.dataY;
        });

    svg.exit()
        .on("click", function(d){
            console.log(d.data.dataX + " " + d.data.dataY);
        })
        .enter()
        .append("text")
        .text("Type: " + data.dataX + " - " + data.dataY);
}

svg.call(plot);
