def watanabe_soil_advice(filename):
    data = open(filename, 'r').read().split()
    fields = []
    for field in data:
        fields.append(field.strip())
        fields.append(int(fields[-1]))

    for field in fields:
        for i in range(1, len(fields)):
            if fields[i] == 0:
                fields[i] = '?'

    return ' '.join(fields).replace('?', '')
