<pre><code>public List&lt;Farm&gt; Get Farms Based on Namespace()
{
    List&lt;Farm&gt; farms = new List&lt;Farm&gt;();
    foreach (string namespace in GetListOfNamespaces())
    {
        Type[] types = AppDomain.CurrentDomain.GetTypes(namespacesToCheck.Contains(namespace) ? namespacesToCheck : new Type[,]());
        foreach (Type type in types)
        {
            if (type.Namespace == namespace && type.Class == "Farm")
            {
                List&lt;Farm&gt; farmList = GetListOfFarms();
                foreach (Farm farm in farmList)
                {
                    Farm currentFarm = new Farm(farm.GetType(), "Current Farm");
                    if (type == typeof(Farm))
                    {
                        currentFarm.GetType().GetMethod("GetFarm").Invoke(currentFarm, null);
                    }
                    else
                    {
                        currentFarm.GetType().GetMethod("GetFarm").Invoke(currentFarm, new object[]{ type.GetType() });
                    }
                }
            }
        }
    }
    return farms;
}</code></pre>

